class visualizeSorting{constructor(t,s){this.list=t}marksorted=async t=>{for(let s=t-1;s>=0;s--)this.list[s].setAttribute("class","bar done")};markmergeSorted=async t=>{this.list[t].setAttribute("class","bar done")};markUnsorted=async t=>{for(let s=t;s<this.list.length;s++)this.list[s].classList.contains("unsorted")||this.list[s].classList.add("unsorted")};unmarkUnsorted=async t=>{for(let s=t;s<this.list.length;s++)this.list[s].classList.remove("unsorted")};markUnsortedPartition=async(t,s)=>{for(let e=t;e<=s;e++)this.list[e].classList.contains("unsorted")||this.list[e].classList.add("unsorted")};unmarkUnsortedPartition=async(t,s)=>{for(let e=t;e<=s;e++)this.list[e].classList.remove("unsorted")};mark=async t=>{this.list[t].setAttribute("class","bar mark")};unmark=async t=>{this.list[t].classList.remove("mark")};markSmaller=async t=>{this.list[t].setAttribute("class","bar smaller")};markGreater=async t=>{this.list[t].setAttribute("class","bar greater")};unmarkSmaller=async t=>{this.list[t].setAttribute("class","bar")};unmarkGreater=async t=>{this.list[t].setAttribute("class","bar")};delay=async()=>new Promise((t=>{setTimeout((()=>{t()}),900/changeSpeed(speed.value))}));compare=async(t,s)=>{await this.delay(),comparisonCount++,parseComparison.querySelector("#comparisonValue").textContent=comparisonCount;return Number(this.list[t].getAttribute("value"))>Number(this.list[s].getAttribute("value"))};findSmaller=async(t,s)=>(await this.markSmaller(t),await this.compare(t,s));findGreater=async(t,s)=>(await this.markGreater(s),await this.compare(t,s));updateList=t=>{this.list=t};moveTo=(t,s)=>{let e,i=this.list[t].dataset.position,a=this.list[t].clientWidth,r=getWidth();e=r<=661?4:r>=662&&r<=992?10:20,t!=s&&t!=s&&(this.list[t].style.transform=t<s?`translateX(${a*(s-i)+(s-i)*Number(e)}px)`:`translateX(${-(a*(i-s)+(i-s)*Number(e))}px)`)};moveToNextPos=async(t,s)=>{await this.moveTo(t,s)};move=(t,s)=>{let e,i=this.list[t].dataset.position,a=this.list[s].dataset.position,r=this.list[t].clientWidth,l=this.list[s].clientWidth,o=getWidth();e=o<=661?4:o>=662&&o<=1080?10:20,this.list[t].style.transform=`translateX(${r*(s-i)+(s-i)*Number(e)}px)`,this.list[s].style.transform=`translateX(${-(l*(a-t)+(a-t)*Number(e))}px)`;let n=[];for(let e=0;e<this.list.length;e++)e==t?n.push(this.list[s]):e==s?n.push(this.list[t]):n.push(this.list[e]);this.list=n};swap=async(t,s)=>(await this.move(t,s),await this.delay(),this.list);markCode=async t=>{pseudoCode.querySelector(`#${t}`).classList.add("markCode")};unmarkCode=async t=>{pseudoCode.querySelector(`#${t}`).classList.remove("markCode")}}